import { queryExecutionClient } from '@dynatrace-sdk/client-query';

const QUERY = `
fetch events, from:now()-30m
| filter event.type == "CUSTOM_DEPLOYMENT"
| filter in(entity_tags, "[Environment]DT_RELEASE_STAGE:simplenode-gitlab-staging")
| summarize count(), alias: eventCount
| fieldsAdd validationId = 10, maxScore = 1, {if(eventCount >= 1, 1, else: 0), alias: finalScore}
| fieldsRemove eventCount
`

export default async function () {
  const timeout = 60;
  const response = await queryExecutionClient.queryExecute({ body: { query: QUERY, requestTimeoutMilliseconds: timeout * 1000, fetchTimeoutSeconds: timeout  } });
  return response.result.records[0];
}

